.s_button {
  border-radius: 28px;
  position: absolute;
  left: 50%;
  top: 50%;
  display: block;
  background: #fff;
  width: 150px;
  box-shadow: 0 2px 6px rgba(170, 185, 200, 0.4);
  -webkit-transform: translate(-50%, -50%);
  transform: translate(-50%, -50%);
}
.s_button svg {
  display: none;
  position: absolute;
  left: 50%;
  top: 50%;
  margin: -15px 0 0 -15px;
  fill: #fff;
}
.s_button div {
  height: 4px;
  margin: -2px 0 0 0;
  position: absolute;
  top: 50%;
  left: 71px;
  right: 25px;
  background: #d3d7e0;
  display: none;
  border-radius: 2px;
}
